-- select name,phone,email,experience_level,idate,vendor_id,IF(vendor_id>=1 AND vendor_id<=10000, 'Tyroo', IF(vendor_id>=10001 AND vendor_id<=20000, 'Trootrac', IF(vendor_id>=2000000 AND vendor_id<=2500000,'internal SEM',''))) AS VendorName, REPLACE(B.text_value,',','') As Salary FROM SumoPlus.resumeoffer_resumeofferlead A LEFT JOIN SumoPlus.lookup_annualsalary B ON A.salary = B.salary_id where date(idate) = date_sub(curdate(), interval 1 day);
select name,phone,email,experience_level,idate,vendor_id,IF(vendor_id>=1 AND vendor_id<=10000, 'Tyroo', IF(vendor_id>=10001 AND vendor_id<=20000, 'Trootrac', IF(vendor_id>=2000000 AND vendor_id<=2500000,'internal SEM',''))) AS VendorName, IF (salary = 1, '1.0 lakh/Yr', IF(salary = 2, '1.0 - 3.0 lakh/Yr', IF(salary = 3, '3.0 - 8.0 lakh/Yr', IF(salary = 4, '8.0 - 15.0 lakh/Yr', IF(salary = 5, '15 lakh/Yr', 'NULL'))))) As Salary FROM SumoPlus.resumeoffer_resumeofferlead where date(idate) = date_sub(curdate(), interval 1 day);
